package exer;

public class IncrementOperatorApr03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int a = 1;
        int b= ++a;//a=a+1 
        System.out.print("a's value:" + a);
        System.out.println(", b's value:" + b);
        
        int c =1;
        int d= c++;
        System.out.print("c's value:" +c );
        System.out.print(", d's value:"+ d );
        
	}

}
